import React, { useState } from "react";
import OtpInput from "react-otp-input";

export default function ModerLogin() {
  const [phoneNumber, setPhoneNumber] = useState("");
  const [otp, setOtp] = useState("");
  const [modal, setModal] = useState(false);
  console.log(modal);

  const handlePhoneNumberChange = (event) => {
    setPhoneNumber(event.target.value);
  };

  // Sms yuborish funktsiyasi
  const handlesmssubmit = () => {
    setModal(false);

    const url = new URL("https://api.frossh.uz/api/auth/verify");

    const headers = {
      "Content-Type": "application/json",
      Accept: "application/json",
    };

    const body = {
      phone_number: phoneNumber,
      code: otp,
    };

    fetch(url, {
      method: "POST",
      headers,
      body: JSON.stringify(body),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
      })
      .catch((error) => {
        console.error("Xato:", error);
      });
  };

  // Formni yuborish funktsiyasi
  const handleSubmit = (event) => {
    event.preventDefault();

    const url = "https://api.frossh.uz/api/auth/login";
    const headers = {
      "Content-Type": "application/json",
      Accept: "application/json",
    };

    const formattedPhoneNumber = `${phoneNumber}`;

    const body = {
      phone_number: formattedPhoneNumber,
    };

    fetch(url, {
      method: "POST",
      headers,
      body: JSON.stringify(body),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
      })
      .catch((error) => {
        console.error("Xatolik:", error);
      });

    setPhoneNumber("");
  };

  return (
    <div className="moderlogin">
      <form onSubmit={handleSubmit}>
        <p>Moderator paneli</p>
        <input
          type="text"
          placeholder="+998"
          name="phoneNumber"
          value={phoneNumber}
          onChange={handlePhoneNumberChange}
        />
        <button type="submit">Sms kod yuborish</button> {/* Formni yuborish */}
      </form>

      <div className="modalsms" style={{ zIndex: modal ? "5" : "-2" }}>
        <p>Sms kodini kiriting</p>
        <OtpInput
          value={otp}
          onChange={setOtp}
          numInputs={6}
          renderSeparator={<span style={{ margin: "0 5px" }}> - </span>}
          renderInput={(props, index) => (
            <input
              {...props}
              key={index}
              style={{
                width: "80px",
                height: "80px",
                textAlign: "center",
                border: "1px solid black",
                borderRadius: "5px",
                margin: "0 5px",
                fontSize: "23px",
              }}
            />
          )}
        />
        <button onClick={handlesmssubmit}>Yuborish</button> {/* Sms yuborish */}
      </div>
    </div>
  );
}
