import React, { useState, useEffect } from "react";
import OtpInput from "react-otp-input";
import { useNavigate } from "react-router-dom";

export default function ModerLogin() {
  const [phoneNumber, setPhoneNumber] = useState("");
  const [otp, setOtp] = useState("");
  const [modal, setModal] = useState(false);
  const navigate = useNavigate();
  const [seconds, setSeconds] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setSeconds(prevSeconds => (prevSeconds === 0 ? 59 : prevSeconds - 1)); 
    }, 1000);

    // Clear interval if seconds reach 0
    if (seconds === 0) {
      clearInterval(interval);
    }

    return () => clearInterval(interval);
  }, [seconds]); // Include seconds in dependency array

  const handlePhoneNumberChange = (event) => {
    setPhoneNumber(event.target.value);
  };

  const handlesmssubmit = () => {
    setModal(false);
    navigate("/moder");

    // Remainder of the function remains the same
    // ...

  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Remainder of the function remains the same
    // ...

  };

  return (
    <div className="moderlogin">
      <form onSubmit={handleSubmit}>
        <p>Moderator paneli</p>
        <input
          type="text"
          placeholder="+998"
          name="phoneNumber"
          value={phoneNumber}
          onChange={handlePhoneNumberChange}
        />
        <button onClick={() => setModal(!modal)} type="submit">
          Sms kod yuborish
        </button>
      </form>

      <div
        className="modalsms"
        style={modal ? { zIndex: "5" } : { zIndex: "-2" }}
      >
        <p>Sms kodini kiriting</p>
        <OtpInput
          value={otp}
          onChange={setOtp}
          numInputs={6}
          renderSeparator={<span style={{ margin: "0 5px" }}>  </span>}
          renderInput={(props, index) => (
            <input
              {...props}
              key={index}
              style={{
                width: "80px",
                height: "80px",
                textAlign: "center",
                border: `1px solid ${seconds === 0 ? "red" : "black"}`,
                borderRadius: "5px",
                margin: "0 5px",
                fontSize: "23px",
              }}
            />
          )}
        />
        <div className="qaytayuborish">
          <span style={{ color: seconds === 0 ? "red" : "black" }}>
            00:{seconds < 10 ? `0${seconds}` : seconds}
          </span>{" "}
          <span>Kod kelmadimi?</span>
          <span className="qayta">Qayta yuborish</span>
        </div>

        <button onClick={handlesmssubmit}>Yuborish</button>
      </div>
    </div>
  );
}
